import streamlit as st
import requests

API_URL = "https://api-inference.huggingface.co/models/jy46604790/Fake-News-Bert-Detect"
headers = {"Authorization": "Bearer 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"}

def query(payload):
    response = requests.post(API_URL, headers=headers, json=payload)
    return response.json()

def get_prediction_label(prediction):
    score = prediction[0]['score']
    if score > 0.5:
        return 'Real', 'https://www.google.com/url?sa=i&url=https%3A%2F%2Fpngtree.com%2Ffreepng%2Fvector-positive-emoji-in-retro-style_8585652.html&psig=AOvVaw3WUaMg53-7mLvcM_Ydc3JC&ust=1714257870844000&source=images&cd=vfe&opi=89978449&ved=0CBIQjRxqFwoTCKix3ca94oUDFQAAAAAdAAAAABAE'
    else:
        return 'Fake', 'data:image/jpeg;base64,'

st.title('Fake News Detection')

user_input = st.text_input('Enter text:')
if st.button('Check'):
    output = query({
        "inputs": user_input,
    })
    prediction_label, image_url = get_prediction_label(output)
